                .loading-dot-2{
                    background-color: var(--warning-80);
                    animation-name: loading-dots-warning;
                }
            }

            &.success{
                .loading-dot-1,
                .loading-dot-2{
                    background-color: var(--success-80);
                    animation-name: loading-dots-success;
                }
            }

            &.white{
                .loading-dot-1,
                .loading-dot-2{
                    background-color: var(--grey-5);
                    animation-name: loading-dots-white;
                }
            }

            &.black{
                .loading-dot-1,
                .loading-dot-2{
                    background-color: var(--grey-8);
                    animation-name: loading-dots-black;
                }
            }
        }
    // #endregion

    // #region Loading animations (Keyframes)
        @keyframes loading-dots-theme{
            from{
                background-color: var(--theme-80);
            }
            50%{
                background-color: var(--theme-60);
            }
            to{
                background-color: var(--theme-80);
            }
        }

        @keyframes loading-dots-grey{
            from{
                background-color: var(--grey-7);
            }
            50%{
                background-color: var(--grey-6);
            }
            to{
                background-color: var(--grey-7);
            }
        }

        @keyframes loading-dots-info{
            from{
                background-color: var(--info-80);
            }
            50%{
                background-color: var(--info-60);
            }
            to{
                background-color: var(--info-80);
            }
        }

        @keyframes loading-dots-error{
            from{
                background-color: var(--error-80);
            }
            50%{
                background-color: var(--error-60);
            }
            to{
                background-color: var(--error-80);
            }
        }

        @keyframes loading-dots-warning{
            from{
                background-color: var(--warning-80);
            }
            50%{
                background-color: var(--warning-60);
            }
            to{
                background-color: var(--warning-80);
            }
        }

        @keyframes loading-dots-success{
            from{
                background-color: var(--success-80);
            }
            50%{
                background-color: var(--success-60);
            }
            to{
                background-color: var(--success-80);
            }
        }

        @keyframes loading-dots-white{
            from{
                background-color: var(--grey-5);
            }
            50%{
                background-color: var(--grey-3);
            }
            to{
                background-color: var(--grey-5);
            }
        }

        @keyframes loading-dots-black{
            from{
                background-color: var(--grey-8);
            }
            50%{
                background-color: var(--grey-6);
            }
            to{
                background-color: var(--grey-8);
            }
        }
    // #endregion
}

